CREATE DATABASE `neonx`
CREATE TABLE `neonx`.`users` ( `id` BIGINT NOT NULL AUTO_INCREMENT , `email` VARCHAR(55) NOT NULL, `password` VARCHAR(255) NOT NULL, `name` VARCHAR(55) NOT NULL , `genero` VARCHAR(255) NOT NULL, `nascimento` DATE,`salt` VARCHAR(255) NOT NULL ,`foto` longblob NOT NULL,`created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`));
CREATE TABLE `neonx`.`consultas` (`idconsulta` BIGINT NOT NULL ,`especialidade` VARCHAR(255) NOT NULL , `horario` VARCHAR(55) NOT NULL , `data` VARCHAR(55) NOT NULL , `clinica` VARCHAR(255) NOT NULL , `peso` VARCHAR(255) NOT NULL, `altura` VARCHAR(255), `nomepaciente` VARCHAR(255) NOT NULL, `medicamentos` VARCHAR(255) NOT NULL ,`created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY(`idconsulta`));
