CREATE DATABASE `neonx`
CREATE TABLE `neonx`.`users` ( `id` BIGINT NOT NULL AUTO_INCREMENT , `email` VARCHAR(55) NOT NULL, `password` VARCHAR(255) NOT NULL, `name` VARCHAR(55) NOT NULL , `genero` VARCHAR(55) NOT NULL, `nascimento` DATE,`salt` VARCHAR(255) NOT NULL ,`foto` longblob NOT NULL,`created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`));
CREATE TABLE `neonx`.`images` (`FotoID` BIGINT NOT NULL AUTO_INCREMENT,`data` BLOB, `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `id` BIGINT NOT NULL ,PRIMARY KEY (`FotoID`),FOREIGN KEY (`id`) REFERENCES `users`(`id`) );
