CREATE DATABASE `neonx`
CREATE TABLE `neonx`.`users` ( `id` BIGINT NOT NULL AUTO_INCREMENT , `email` VARCHAR(55) NOT NULL, `password` VARCHAR(255) NOT NULL, `name` VARCHAR(55) NOT NULL , `genero` VARCHAR(255) NOT NULL, `nascimento` DATE,`salt` VARCHAR(255) NOT NULL ,`foto` longblob NOT NULL,`created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`));
CREATE TABLE `neonx`.`consultas` (`id` BIGINT NOT NULL ,`nome_consulta` VARCHAR(55) NOT NULL , `horario` VARCHAR(55) NOT NULL , `data` VARCHAR(55) NOT NULL , `clinica` VARCHAR(255) NOT NULL ,`created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY(`id`));
CREATE TABLE `neonx`.`especialidade` (`id` BIGINT NOT NULL AUTO_INCREMENT ,`nome` VARCHAR(55) NOT NULL , `horario` VARCHAR(55) NOT NULL , `data` VARCHAR(55) NOT NULL , `clinica` VARCHAR(255) NOT NULL , `endereco` VARCHAR(255) NOT NULL, `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY(`id`));
